package modelo;

public class Prenda {
	private int idprenda;
	private String nomprenda;
	private String descprenda;
	private double pesoprenda;
	private int cantidadprenda;
	//
	public Prenda() {
		// TODO Auto-generated constructor stub
	}
	//
	public int getIdprenda() {
		return idprenda;
	}
	public void setIdprenda(int idprenda) {
		this.idprenda = idprenda;
	}
	public String getNomprenda() {
		return nomprenda;
	}
	public void setNomprenda(String nomprenda) {
		this.nomprenda = nomprenda;
	}
	public String getDescprenda() {
		return descprenda;
	}
	public void setDescprenda(String descprenda) {
		this.descprenda = descprenda;
	}
	public double getPesoprenda() {
		return pesoprenda;
	}
	public void setPesoprenda(double pesoprenda) {
		this.pesoprenda = pesoprenda;
	}
	public int getCantidadprenda() {
		return cantidadprenda;
	}
	public void setCantidadprenda(int cantidadprenda) {
		this.cantidadprenda = cantidadprenda;
	}
}
